INSERT INTO post (id, title, organization, post_image, description, support_amount, target_amount, start_date, end_date, status)
VALUES
    (1, '어르신에게 따뜻한 봄날을 선물해 주세요!','사회복지관', '1.png', '가정의달 5월이 다가오고 있습니다. 매년 5월 8일은 어버이날로 우리에게 매우 소중한 날 중 하나 입니다. 우리는 어버이 사랑과 감사를 전하는 시간을 가지고, 부모님이 우리 인생에 얼마나 중요한 영향력을 끼쳤는지 되새겨보는 시간을 함께 가지며 가족의 정을 나누고 행복한 시간을 가집니다. 하지만 누구보다 행복한 시간을 보내야할 어버이 중에는 오히려 고독한 하루를 보내는 분들도 있습니다. 의성군 금성면 남OO 어르신은 과거 아내와 사별하고 30년 가까이 홀로 생활하고 있습니다. 자녀들은 자주 오는지 여쭙는 질문에 “자녀들도 사는 게 힘들어서 못 와.”라고 씁쓸한 표정으로 대답하십니다. 그래도 자녀들이 잘 살았으면 좋겠다는 말씀을 덧붙이며 오히려 자식들을 걱정하고 지지해 주시는 모습에 안타까운 마음이 듭니다.', 500000, 1000000, '2024-03-29', '2024-04-20', 'ING'),
    (2, '반려동물을 위한 나눔의 마음', '동물보호협회', '2.png', '우리의 가족, 반려동물을 위해 나눔의 손길을 보내봐요. 함께 따뜻한 마음을 전합니다.', 800000, 900000, '2024-04-01', '2024-05-01', 'ING'),
    (3, '어린이들에게 희망의 봄날을', '어린이재단', '3.png', '결식아동들에게 희망과 사랑을 전합니다. 우리의 작은 마음이 큰 힘이 될 수 있어요.', 1000000, 1200000, '2024-03-15', '2024-04-15', 'ING'),
    (4, '따뜻한 겨울을 선물하세요', '사회복지관', '4.png', '추운 겨울에 기저귀, 옷, 이불 등의 필수품이 부족한 가정들을 돕기 위해 기부하세요. 이번 캠페인은 취약계층을 위한 필수품 지원 및 생활용품 제공을 통해 따뜻한 겨울을 보내도록 돕습니다. 우리의 작은 동행이 더 많은 이웃들에게 행복을 전달합니다!', 800000, 900000, '2024-03-20', '2024-04-20', 'ING'),
    (5, '반려동물과 함께하는 봄날', '동물보호협회', '5.png', '우리의 반려동물과 함께 봄을 즐기며, 그들에게 사랑을 전합니다. 함께 행복한 시간을 보내봐요.', 1000000, 1500000, '2024-04-10', '2024-05-10', 'ING'),
    (6, '결식아동들을 위한 나눔', '어린이재단', '6.png', '힘들고 어려운 환경에서 자라나는 결식아동들에게 우리의 사랑을 전합니다. 함께 희망의 빛을 밝혀봐요.', 900000, 1100000, '2024-04-05', '2024-05-05', 'ING'),
    (7, '독거노인에게 희망의 손길을', '사회복지관', '7.png', '홀로 지내는 독거노인들에게 우리의 사랑을 전합니다. 함께 따뜻한 마음을 보내봐요.', 350000, 780000, '2024-03-25', '2024-04-25', 'ING'),
    (8, '반려동물을 위한 봄날의 선물', '동물보호협회', '8.png', '우리의 반려동물들에게 사랑과 행복을 선물합니다. 함께 따뜻한 마음을 전해봐요.', 900000, 1000000, '2024-03-28', '2024-04-28', 'ING'),
    (9, '결식아동에게 희망을', '어린이재단', '9.png', '어려운 상황에서 자라나는 결식아동들에게 희망의 빛을 보내봐요. 함께 미래를 밝혀봐요.', 750000, 850000, '2024-04-07', '2024-05-07', 'ING'),
    (10, '어린이들의 꿈 이루기 캠페인', '어린이재단', '10.png', '우리의 작은 기부로 어린이들의 꿈을 이루어주세요. 이번 캠페인은 교육을 받지 못하는 어린이들에게 희망의 기회를 제공하고, 그들의 미래를 밝히는 데 도움을 줍니다. 함께해요!', 800000, 950000, '2024-04-15', '2024-05-15', 'ING'),
    (11, '자연 보호 캠페인: 우리의 지구를 지켜주세요', '녹색연합', '11.png', '지구의 생태계를 보호하고 지속 가능한 미래를 위해 기부하세요. 이번 캠페인은 산림 보호, 해양 정화, 환경 교육 등 다양한 분야에서 활동하고 있습니다. 우리의 작은 노력이 큰 변화를 만듭니다!', 500000, 950000, '2024-04-15', '2024-05-15', 'ING'),
    (12, '함께하는 마음으로', '동물보호협회', '12.png', '유기동물을 위한 임시 보호소 건립에 기부하세요. 이번 캠페인은 유기동물들에게 안전한 보금자리를 제공하고, 입양을 통해 새로운 가정을 찾을 수 있도록 돕습니다. 우리의 작은 동행이 동물들에게 큰 희망을 줍니다!', 750000, 950000, '2024-04-15', '2024-05-15', 'ING'),
    (13, '영양의 손길을 전하다', '사회복지관', '13.png', '모든 사람에게 영양이 있는 식사를 제공하기 위해 기부하세요. 이번 캠페인은 식량 및 건강한 식사 재료를 필요로 하는 이웃들에게 도움을 주고, 영양 부족을 해결하기 위해 노력합니다. 우리의 작은 식품 기부가 더 많은 사람들에게 건강과 희망을 제공합니다.', 800000, 950000, '2024-04-15', '2024-05-15', 'ING'),
    (14, '지구를 위한 1시간 불 끄기! 어스아워', '환경연합', '14.png', '지구에 쉼을 선물하는 한시간, 어스아워(Earth Hour) 올해로 17주년을 맞이하는 어스아워 캠페인은 기후 변화와 자연파괴의 심각성을 알리기 위해 매년 3월 마지막 주 토요일 저녁 8시30분부터 1시간 동안 불을 끄는 글로벌 자연 보전 캠페인입니다. 전 세계 190여개국 지구 시민이 참여하는 어스아워는 세계 최대 규모의 자연 보전 캠페인으로 WWF가 2007년 호주 시드니에서 처음 시작하였습니다. 비록 1시간 동안 불을 끄는 단순하지만 상징적인 행동을 통해 우리가 함께 지구에게 쉼을 주자는 메시지를 전하고 있습니다. 현재 남산서울타워, 국회의사당, 프랑스 에펠탑, 호주의 오페라 하우스 등 세계 주요 랜드마크부터 집 근처의 편의점까지, 다양한 주체들이 희망과 연대의 상징인 ‘1시간 소등’에 참여하고 있습니다. 지구를 살리는 1시간, 여의도 면적의 숲이 살아나고, 그물에 갇힌 30마리의 거북이가 살아나고, 3가지 종류의 멸종위기 동식물이 살아날 수 있는 시간입니다.', 800000, 950000, '2024-01-15', '2024-03-15', 'END'),
    (15, '다양성과 포용을 위한 노력', '사회복지관', '15.png', '장애를 가진 이웃들에게 삶의 질을 높이고 포용력 있는 사회를 만들기 위해 기부하세요. 이번 캠페인은 장애인들을 위한 보조기기 제공, 직업 교육 프로그램 지원 등을 통해 그들의 능력을 인정하고 지원합니다. 우리의 작은 노력이 더 포용력 있는 사회를 만듭니다!', 1000000, 1000000, '2024-03-01', '2024-04-05', 'END');
